graphql.query:
query {
    companies {
        nodes {
            id
            createdAt
            updatedAt
            name
            logo {
                id
                url
            }
            appTheme
            sharingRedirectUrl
            pwaShortName
            pwaIcon {
                id
                url
            }
            pwaThemeColor
            pwaBackgroundColor
            branches {
                nodes {
                    id
                    createdAt
                    updatedAt
                    name
                    welcomeMessage
                    phoneNumber {
                        raw
                        href
                    }
                    address
                    image {
                        id
                        url
                    }
                    logo {
                        id
                        url
                    }
                    appTheme
                    sharingRedirectUrl
                    imprint
                    # company
                    openingHours {
                        formatted {
                            key
                            closed
                            break
                            shortDay
                            fullDay
                            value
                        }
                        raw {
                            day
                            closed
                            break
                            shortDay
                            fullDay
                            forenoon {
                                start
                                end
                            }
                            afternoon {
                                start
                                end
                            }
                        }
                    }
                    reviewAggregations {
                        totalCount
                        averageRating
                        countPerStar
                    }
                    reviews {
                        nodes {
                            id
                            createdAt
                            updatedAt
                            stars
                            title
                            text
                            type
                            status
                            customer {
                                type
                                id
                                image {
                                    id
                                    url
                                }
                                createdAt
                                updatedAt
                                email
                                firstName
                                lastName
                                fullName
                                gender
                                note
                                phoneNumber {
                                    raw
                                    href
                                }
                                appointments {
                                    nodes {
                                        id
                                        createdAt
                                        updatedAt
                                        desc
                                        start
                                        end
                                        price {
                                            value
                                            displayValue
                                        }
                                        duration
                                        status
                                        note
                                        beforeImage {
                                            id
                                            url
                                        }
                                        afterImage {
                                            id
                                            url
                                        }
                                        employee {
                                            type
                                            id
                                            image {
                                                id
                                                url
                                            }
                                            createdAt
                                            updatedAt
                                            email
                                            firstName
                                            lastName
                                            fullName
                                            gender
                                            phoneNumber {
                                                raw
                                                href
                                            }
                                            # appointments
                                        }
                                        # customer
                                    }
                                }
                                # reviews
                            }
                            appointment {
                                id
                                createdAt
                                updatedAt
                                desc
                                start
                                end
                                price {
                                    value
                                    displayValue
                                }
                                duration
                                status
                                note
                                beforeImage {
                                    id
                                    url
                                }
                                afterImage {
                                    id
                                    url
                                }
                                employee {
                                    type
                                    id
                                    image {
                                        id
                                        url
                                    }
                                    createdAt
                                    updatedAt
                                    email
                                    firstName
                                    lastName
                                    fullName
                                    gender
                                    phoneNumber {
                                        raw
                                        href
                                    }
                                    # appointments
                                }
                                # customer
                            }
                        }
                    }
                }
            }
        }
    }
}

