query {
    customers(input:{}) {
        nodes {
            type
            id
            image {
                id
                url
            }
            createdAt
            updatedAt
            email
            firstName
            lastName
            fullName
            gender
            note
            phoneNumber {
                raw
                href
            }
            street
            zipCode
            city
            birthday
            appointments {
                nodes {
                    id
                    createdAt
                    updatedAt
                    desc
                    start
                    end
                    price {
                        value
                        displayValue
                    }
                    duration
                    status
                    note
                    beforeImage {
                        id
                        url
                    }
                    afterImage {
                        id
                        url
                    }
                    employee {
                        type
                        id
                        image {
                            id
                            url
                        }
                        createdAt
                        updatedAt
                        email
                        firstName
                        lastName
                        fullName
                        gender
                        phoneNumber {
                            raw
                            href
                        }
                        # appointments
                    }
                    # customer
                    products {
                        item {
                            id
                            createdAt
                            updatedAt
                            name
                            desc
                            price {
                                value
                                displayValue
                            }
                            capacity
                            category
                            image {
                                id
                                url
                            }
                            attributes {
                                key
                                name
                                value
                            }
                        }
                        count
                    }
                    services {
                        id
                        createdAt
                        updatedAt
                        name
                        desc
                        price {
                            value
                            displayValue
                        }
                        duration
                        genderTarget
                        image {
                            id
                            url
                        }
                        attributes {
                            key
                            name
                            value
                        }
                    }
                }
            }
            reviews {
                nodes {
                    id
                    createdAt
                    updatedAt
                    stars
                    title
                    text
                    type
                    # customer
                    ... on ProductReview {
                        product {
                            id
                            name
                        }
                    }
                    ... on ServiceReview {
                        service {
                            id
                            name
                        }
                    }
                }
            }
        }
    }
}

